    }
  }

  .add {
    text-align: left;
    padding-left: 50px;
    line-height: 1.5em;
    font-size: 1.1rem;
  }

  .start {
    margin: 15px;
    padding: 10px 25px;
    font-size: 20px;
    border-radius: 10px;
    background-color: orange;
    color: white;

    &:hover {
      background-color: darken(orange, 5%);
    }
  }
}

#popUp {
  border-radius: 30px;
  overflow: hidden;
  z-index: 10;
  background-color: #fff;
  width: 700px;
