      }
    }

    .answer {
      margin-top: 30px;
      display: block;
      font-size: 30px;
      color: rgb(236, 112, 24);
    }
  }

  .selector {
    position: absolute;
    display: flex;
    width: 100%;
    bottom: 0;
    left: 0;

    .btn {
      text-align: center;
      width: 50%;
      height: 80px;
      line-height: 80px;
      font-size: 1.5rem;
      font-weight: 500;
      background-color: orange;
      color: #fff;
      &:hover {
        background-color: darken(orange, 5%);
      }
    }

    .btn--redo {
      border-right: 1px solid #fff;
    }
  }
}

.overlay {
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background-color: rgba(0, 0, 0, 0.582);
  z-index: 9;
