          td {
            padding-top: 10px;
          }
        }

        .out {
          color: crimson;
          text-shadow: 0 0 15px crimson;
        }

        td {
          text-align: center;
          padding-top: 5px;

          &:first-child {
            color: #fff;
            text-shadow: 0 0 15px #fff;
          }
        }
      }


    }

  }

  .submit {
    position: absolute;
    left: 50%;
    transform: translateX(-50%);
    bottom: -5px;
    width: 45%;
    height: 60px;
    z-index: 10;
    border: 7px solid cadetblue;
    display: flex;

    input {
      display: block;
      border: none;
    }

    input[type=text] {
      width: 80%;
      outline: none;
      font-size: 40px;
      text-align: center;
      font-family: 'Keania One', cursive;
    }

    input[type=submit] {
      width: 20%;
      font-size: 25px;
    }
  }
